CREATE TABLE "announcements" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "title" TEXT, "message" TEXT, "author" TEXT, "from_time" INTEGER DEFAULT 0, "to_time" INTEGER DEFAULT 0, "post_date" INTEGER DEFAULT 0, "active" INTEGER DEFAULT 1);
CREATE TABLE "servers" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "ip" TEXT DEFAULT "127.0.0.1", "hostname" TEXT DEFAULT "localhost", "connected" INTEGER DEFAULT 0, "active" INTEGER DEFAULT 1, "username" TEXT, "password" TEXT);
CREATE TABLE "settings" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "use_mumble" INTEGER DEFAULT 1, "mumble_tcp" INTEGER DEFAULT 0, "use_codec2" INTEGER DEFAULT 0, "audio_treshhold" INTEGER DEFAULT -15, "voice_activation" FLOAT DEFAULT 0.5, "voice_activation_timeout" INTEGER DEFAULT 50, "voice_server_port" INTEGER DEFAULT 64738, "local_udp_port" INTEGER DEFAULT 4938, "control_port" INTEGER DEFAULT 4939, "opus_bitrate" INTEGER DEFAULT 8000, "codec2_bitrate" INTEGER DEFAULT 1200, "enable_vox" INTEGER DEFAULT 1, "enable_agc" INTEGER DEFAULT 1, "ident_time" INTEGER DEFAULT 300, "radio_id" TEXT, "callsign" TEXT, "voice_server_ip" TEXT);
CREATE TABLE "stations" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "callsign" TEXT, "radio_id" TEXT, "ip" TEXT DEFAULT "127.0.0.1", "hostname" TEXT DEFAULT "localhost", "in_call" INTEGER DEFAULT 0, "conference_id" TEXT, "called_by" INTEGER DEFAULT 0, "call_time" INTEGER DEFAULT 0, "repeater" INTEGER DEFAULT 0, "local" INTEGER DEFAULT 0, "active" INTEGER DEFAULT 1);
CREATE TABLE "voicemail" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "file_name" TEXT);
